
const input = `



`;

const nums = [[ 1, 2, 3 ], [ 4, 5, 6 ], [ 7, 8, 9]];

const rows = input.split('\n');

const dirs = {
  U: {
    key: 0,
    dir: -1,
  },
  D: {
    key: 0,
    dir: 1,
  },
  L: {
    key: 1,
    dir: -1,
  },
  R: {
    key: 1,
    dir: 1,
  },
};

const presses = [];

for (let i=0, l=rows.length, position = [1, 1]; i<l; i++) {
  let row = rows[i].split('');
  let move;
  while ((move = row.shift())) {
    let {
      key,
      dir,
    } = dirs[move];

    position[key] += dir;
    if (position[key] < 0) position[key] = 0;
    if (position[key] > 2) position[key] = 2;
  }
  const press = nums[position[0]][position[1]];
  presses.push(press);
}

console.info('presses', presses.join(''));
